mapper.select:
select m.PERMS from M_ROLE r left join M_USER_ROLE ur on(r.ROLE_ID = ur.ROLE_ID) left join M_USER u on(u.USER_ID = ur.USER_ID) left join M_ROLE_MENU rm on(rm.ROLE_ID = r.ROLE_ID) left join M_MENU m on(m.MENU_ID = rm.MENU_ID ) where 1=1
AND u.USERNAME = #{userName}
and m.PERMS is not null and m.PERMS <> ''

select m.* from M_MENU m where m.MENU_ID in (select distinct rm.MENU_ID from M_ROLE_MENU rm left join M_ROLE r on(rm.ROLE_ID = r.ROLE_ID) left join M_USER_ROLE ur on(ur.ROLE_ID = r.ROLE_ID) left join M_USER u on (u.USER_ID = ur.USER_ID) where 1=1
AND u.USERNAME = #{userName}
)order by m.ORDER_NUM

mapper.update:
update M_MENU set PARENT_ID = 0 where MENU_ID in ( select a.MENU_ID from (
(select MENU_ID from M_MENU where PARENT_ID in

	#{menuId}

) ) a )
