mapper.select:
  		SELECT U.USER_ID USERID,U.USERNAME,U.EMAIL,U.MOBILE,U.STATUS,U.CRATE_TIME CRATETIME,
  		U.SSEX,D.GROUP_NAME GROUPNAME,U.REAL_NAME REALNAME   FROM T_USER U
  		LEFT JOIN M_GROUP D ON(U.GROUP_ID = D.GROUP_ID AND D.GROUP_TYPE='2')
  		WHERE 1=1

        	AND U.USERNAME = #{username}


	        AND D.GROUP_ID = #{groupId}


	        AND U.SSEX = #{ssex}


	        AND U.STATUS = #{status}

  		order by U.USER_ID
